package com.cts.training.quotient;

public class Quotient {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int a=13,b=2;
         int q= a/b;
         int r=a%b;
         System.out.println("quotient is: " + q);
         System.out.println("remainder is :" +r);
      
	}

}
